DefaultTemplateParameters.SYNC_AVIONICS_TO_BATTERY_STATE:
(A:ELECTRICAL MASTER BATTERY:1, Bool) (>O:_AvionicsShouldBeOn)
1 (>A:BUS LOOKUP INDEX, Number)
(O:_AvionicsShouldBeOn) (A:BUS CONNECTION ON:1, Bool) != if{
1 1 (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
}
(O:_AvionicsShouldBeOn) (A:CIRCUIT SWITCH ON:19, Bool) != if{
19 (>K:ELECTRICAL_CIRCUIT_TOGGLE)
}

UseInputEvent.ON_AUTOSTART:
(A:GENERAL ENG COMBUSTION:1, bool) ! if{
	0.025 (>L:XMLVAR_PUMPED_FUEL)
	17 (>A:GENERAL ENG THROTTLE LEVER POSITION:1, percent)
	95 (>A:GENERAL ENG MIXTURE LEVER POSITION:1, percent)
	1 (>K:ALTERNATOR_SET)
	1 (>A:ELECTRICAL MASTER BATTERY:1, bool)
	(A:GENERAL ENG FUEL VALVE:1, bool) ! if{
		(>K:TOGGLE_FUEL_VALVE_ENG1)
		(>K:MAGNETO1_START)
	} els{
		(>K:MAGNETO1_START)
	}
	(A:BRAKE PARKING INDICATOR, bool) ! if{
		(>K:PARKING_BRAKES)
	}
}

UseTemplate.TOOLTIP_ALTIMETER_TITLE:
(E:UNITS OF MEASURE, number) 0 == if{
	(A:KOHLSMAN SETTING HG, inhg) 'Altimeter Calibration (%1.2f)' (F:Format)
}
(E:UNITS OF MEASURE, number) 0 > if{
	(A:KOHLSMAN SETTING MB, millibars) 'Altimeter Calibration (%d)' (F:Format)
}

